import React, {useContext, useEffect, useState} from 'react';
import axios from "axios";
import {APIConfig} from "../../store/API-Config";
import store from "../../store/store";

const Approval = (props)=>{
    const APIs = useContext(APIConfig);
    const [sellers,setSellers] = useState([]);
    const state = store.getState();
    const headers = {
        'Access-Control-Allow-Origin': '*',
        'Authorization': 'Bearer ' + state.oAuthToken,
    }
    const approveHandler = (seller) => {
        axios(APIs.adminAPI + '/approve?seller=' + seller.id , {headers})
            .then(response => {
            if(response.data === true){
                seller.approved = true;
                loadData();
            }
        }).catch(error => {
            alert(error.message);
        })
    };

    const loadData = ()=>{
        axios(APIs.sellerAPI,{headers})
            .then(response=>{
                const info = JSON.stringify(response.data);
                setSellers(response.data);
            }).catch(error => {
            alert(error.message);
        })
    }
    useEffect(()=>{
        loadData();
    },[]);
return (
   <div>
       <h1>List of Sellers</h1>
       <table className="table">
           <thead>
           <tr>
               <th>ID</th>
               <th>NAME</th>
               <th>APPROVED</th>
           </tr>
           </thead>
           <tbody>
           {sellers && sellers.map((seller) => (
               <tr key={seller.id}>
                   <td>{seller.id}</td>
                   <td>{seller.user.firstName} {seller.user.lastName}</td>
                   <td>
                       {!seller.approved && (
                           <button
                               type="button"
                               className="small"
                               onClick={() => approveHandler(seller)}
                           >
                               Approve
                           </button>
                       ) }
                   </td>
               </tr>
           ))}
           </tbody>
       </table>
   </div>
);
}

export  default Approval;
